import React, { useState } from 'react'
import { MDBInput } from 'mdb-react-ui-kit';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';

function Edit() {

  //to hold the updated values
  const[eid,setId]=useState("")
  const[empname,setName]=useState("")
  const[empage,setAge]=useState("")
  const[empdesignation,setDesignation]=useState("")


  //get a particular id from the url
  const {id} =useParams()
  console.log(id);

  const base_url = 'http://localhost:8000'
  //api call to update an employee details
  const updateEmployee =async(e)=>{
    const body ={
      id,name,age,designation,salary
    }
    const result=await axios.post(`${base_url}/update-an-employee/${id}`,body)
    console.log(result);

  }

  
  return (
    <div>
       <div className="container text-center m-5">
            <h2>Edit Employee</h2>
            <form className='p-5'>
            <MDBInput onChange={(e)=>setId(e.target.value)} label='ID' id='formControlLg' type='text' size='lg' />
            <br />
            <MDBInput onChange={(e)=>setName(e.target.value)} label='Name' id='formControlLg' type='text' size='lg' />
            <br />
            <MDBInput onChange={(e)=>setAge(e.target.value)} label='Age' id='formControlLg' type='text' size='lg' />
            <br />
            <MDBInput onChange={(e)=>setDesignation(e.target.value)} label='Designation' id='formControlLg' type='text' size='lg' />
            <br />
            <MDBInput onChange={(e)=>setSalary(e.target.value)} label='Salary' id='formControlLg' type='text' size='lg' />
            <br />
            <div>
                <button onClick={(e)=>updateEmployee(e)} className='btn btn-success m-3'>Update <i className='fa-solid fa-user-plus'></i></button>
            </div>

            </form>
        </div>
    </div>
  )
}

export default Edit
